import { useMutation } from '@tanstack/react-query'

import { AxiosError } from 'axios'

import instance from '@/configs/axios/instance'

import {
  AccountErrorMessageType,
  CommonErrorType,
} from '../@types/data-contracts'
import { MutationHookParams } from '../@types/react-query-type'
import { AccountApi } from './Account.api'

export const accountApi = new AccountApi({ instance: instance })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */

export const QUERY_KEY_ACCOUNT_API = {
  VERIFY_CREATE: () => ['ACCOUNT_VERIFY_CREATE'],
}

/**
 * No description
 *
 * @tags account
 * @name AccountVerifyCreate
 * @summary 계좌 검증
 * @request POST:/v1/account/verify/
 * @secure  */

export const useAccountVerifyCreateMutation = (
  params: MutationHookParams<
    typeof accountApi.accountVerifyCreate,
    AxiosError<AccountErrorMessageType | CommonErrorType>
  >,
) => {
  const mutationKey = QUERY_KEY_ACCOUNT_API.VERIFY_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: accountApi.accountVerifyCreate,
    ...params?.options,
  })
}

