import { useQuery } from '@tanstack/react-query'

import { AxiosError } from 'axios'

import instance from '@/configs/axios/instance'

import { CommonErrorType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { DisclaimerApi } from './Disclaimer.api'

export const disclaimerApi = new DisclaimerApi({ instance: instance })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */

export const QUERY_KEY_DISCLAIMER_API = {
  RETRIEVE: (variables?: Parameter<typeof disclaimerApi.disclaimerRetrieve>) =>
    ['DISCLAIMER_RETRIEVE', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags disclaimer
 * @name DisclaimerRetrieve
 * @summary 고지사항 상세 조회
 * @request GET:/v1/disclaimer/{kind}/
 * @secure    */

export const useDisclaimerRetrieveQuery = <
  TData = RequestFnReturn<typeof disclaimerApi.disclaimerRetrieve>,
>(
  params: QueryHookParams<
    typeof disclaimerApi.disclaimerRetrieve,
    AxiosError<CommonErrorType>,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_DISCLAIMER_API.RETRIEVE(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => disclaimerApi.disclaimerRetrieve(params.variables),
    ...params?.options,
  })
}

