import { useQuery } from '@tanstack/react-query'

import { AxiosError } from 'axios'

import instance from '@/configs/axios/instance'

import { CommonErrorType } from '../@types/data-contracts'
import {
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { FaqApi } from './Faq.api'

export const faqApi = new FaqApi({ instance: instance })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */

export const QUERY_KEY_FAQ_API = {
  LIST: (variables?: Parameter<typeof faqApi.faqList>) =>
    ['FAQ_LIST', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags faq
 * @name FaqList
 * @summary 자주하는 질문 목록 조회
 * @request GET:/v1/faq/
 * @secure    */

export const useFaqListQuery = <TData = RequestFnReturn<typeof faqApi.faqList>>(
  params?: QueryHookParams<
    typeof faqApi.faqList,
    AxiosError<CommonErrorType>,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_FAQ_API.LIST(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => faqApi.faqList(params?.variables),
    ...params?.options,
  })
}

