import { ContentType, HttpClient, RequestParams } from '../@http-client'
import {
  CommonErrorType,
  GovLoginErrorMessageType,
  GovLoginRequestType,
  GovLoginType,
  GovOtpErrorMessageType,
  GovOtpRequestType,
  GovType,
} from '../@types/data-contracts'
import { DeepOmitReadOnly } from '../@types/util-types'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class GovApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags gov
   * @name GovLoginCreate
   * @summary 정부24 로그인
   * @request POST:/v1/gov/login/
   * @secure
   */
  govLoginCreate = (variables: {
    data: DeepOmitReadOnly<GovLoginRequestType>
    params?: RequestParams
  }) =>
    this.request<GovLoginType, GovLoginErrorMessageType | CommonErrorType>({
      path: `/v1/gov/login/`,
      method: 'POST',
      body: variables.data,
      secure: true,
      type: ContentType.Json,
      format: 'json',
      ...variables.params,
    })

  /**
   * No description
   *
   * @tags gov
   * @name GovRetrieve
   * @summary 정부24 상세 조회
   * @request GET:/v1/gov/{id}/
   * @secure
   */
  govRetrieve = (variables: { id: number; params?: RequestParams }) =>
    this.request<GovType, CommonErrorType>({
      path: `/v1/gov/${variables.id}/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    })

  /**
   * No description
   *
   * @tags gov
   * @name GovOtpCreate
   * @summary 정부24 OTP
   * @request POST:/v1/gov/{id}/otp/
   * @secure
   */
  govOtpCreate = (variables: {
    id: number
    data: DeepOmitReadOnly<GovOtpRequestType>
    params?: RequestParams
  }) =>
    this.request<void, GovOtpErrorMessageType | CommonErrorType>({
      path: `/v1/gov/${variables.id}/otp/`,
      method: 'POST',
      body: variables.data,
      secure: true,
      type: ContentType.Json,
      ...variables.params,
    })
}

