import { useMutation, useQuery } from '@tanstack/react-query'

import { AxiosError } from 'axios'

import instance from '@/configs/axios/instance'

import {
  CommonErrorType,
  GovLoginErrorMessageType,
  GovOtpErrorMessageType,
} from '../@types/data-contracts'
import {
  MutationHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { GovApi } from './Gov.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const govApi = new GovApi({ instance: instance })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_GOV_API = {
  LOGIN_CREATE: () => ['GOV_LOGIN_CREATE'],
  RETRIEVE: (variables?: Parameter<typeof govApi.govRetrieve>) =>
    ['GOV_RETRIEVE', variables].filter(isDefined),
  OTP_CREATE: () => ['GOV_OTP_CREATE'],
}

/**
 * No description
 *
 * @tags gov
 * @name GovLoginCreate
 * @summary 정부24 로그인
 * @request POST:/v1/gov/login/
 * @secure  */

export const useGovLoginCreateMutation = (
  params: MutationHookParams<
    typeof govApi.govLoginCreate,
    AxiosError<GovLoginErrorMessageType | CommonErrorType>
  >,
) => {
  const mutationKey = QUERY_KEY_GOV_API.LOGIN_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: govApi.govLoginCreate,
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags gov
 * @name GovRetrieve
 * @summary 정부24 상세 조회
 * @request GET:/v1/gov/{id}/
 * @secure    */

export const useGovRetrieveQuery = <
  TData = RequestFnReturn<typeof govApi.govRetrieve>,
>(
  params: QueryHookParams<
    typeof govApi.govRetrieve,
    AxiosError<CommonErrorType>,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_GOV_API.RETRIEVE(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => govApi.govRetrieve(params.variables),
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags gov
 * @name GovOtpCreate
 * @summary 정부24 OTP
 * @request POST:/v1/gov/{id}/otp/
 * @secure  */

export const useGovOtpCreateMutation = (
  params: MutationHookParams<
    typeof govApi.govOtpCreate,
    AxiosError<GovOtpErrorMessageType | CommonErrorType>
  >,
) => {
  const mutationKey = QUERY_KEY_GOV_API.OTP_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: govApi.govOtpCreate,
    ...params?.options,
  })
}
