import { HttpClient, RequestParams } from '../@http-client'
import {
  CommonErrorType,
  NoticeDetailType,
  PaginatedNoticeListType,
} from '../@types/data-contracts'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class NoticeApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags notice
   * @name NoticeList
   * @summary 공지사항 목록 조회
   * @request GET:/v1/notice/
   * @secure
   */
  noticeList = (variables?: {
    query?: {
      /** 검색어 */
      q?: string
      /** 고정여부 */
      is_pinned?: boolean
      /** Number of results to return per page. */
      limit?: number
      /** The initial index from which to return the results. */
      offset?: number
    }
    params?: RequestParams
  }) =>
    this.request<PaginatedNoticeListType, CommonErrorType>({
      path: `/v1/notice/`,
      method: 'GET',
      query: variables?.query,
      secure: true,
      format: 'json',
      ...variables?.params,
    })

  /**
   * No description
   *
   * @tags notice
   * @name NoticeRetrieve
   * @summary 공지사항 상세 조회
   * @request GET:/v1/notice/{id}/
   * @secure
   */
  noticeRetrieve = (variables: { id: number; params?: RequestParams }) =>
    this.request<NoticeDetailType, CommonErrorType>({
      path: `/v1/notice/${variables.id}/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    })
}

