import { HttpClient, RequestParams } from '../@http-client'
import { CommonErrorType, OrderType } from '../@types/data-contracts'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class OrderApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags order
   * @name OrderRetrieve
   * @summary 주문 상세 조회
   * @request GET:/v1/order/{code}/
   * @secure
   */
  orderRetrieve = (variables: { code: string; params?: RequestParams }) =>
    this.request<OrderType, CommonErrorType>({
      path: `/v1/order/${variables.code}/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    })
}

