/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const prepaymentApi = new PrepaymentApi({ instance: instance })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_PREPAYMENT_API = {
  LOAN_PREPAYMENT_CREATE: () => ['LOAN_PREPAYMENT_CREATE'],
  LOAN_PREPAYMENT_CALCULATE_CREATE: () => ['LOAN_PREPAYMENT_CALCULATE_CREATE'],
}

/**
 * No description
 *
 * @tags prepayment
 * @name LoanPrepaymentCreate
 * @summary 중도상환 등록
 * @request POST:/v1/loan/{loan_id}/prepayment/
 * @secure  */

export const useLoanPrepaymentCreateMutation = (
  params: MutationHookParams<
    typeof prepaymentApi.loanPrepaymentCreate,
    AxiosError<PrepaymentErrorMessageType | CommonErrorType>
  >,
) => {
  const mutationKey = QUERY_KEY_PREPAYMENT_API.LOAN_PREPAYMENT_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: prepaymentApi.loanPrepaymentCreate,
    ...params?.options,
  })
}

