export class ScheduleApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags schedule
   * @name LoanScheduleList
   * @summary 일정 목록 조회
   * @request GET:/v1/loan/{loan_id}/schedule/
   * @secure
   */
  loanScheduleList = (variables: { loanId: number; params?: RequestParams }) =>
    this.request<ScheduleType[], CommonErrorType>({
      path: `/v1/loan/${variables.loanId}/schedule/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    })
}

