import { HttpClient, RequestParams } from '../@http-client'
import { CommonErrorType, SettingType } from '../@types/data-contracts'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class SettingApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags setting
   * @name SettingRetrieve
   * @summary Setting 상세 조회
   * @request GET:/v1/setting/{id}/
   * @secure
   */
  settingRetrieve = (variables: { id: string; params?: RequestParams }) =>
    this.request<SettingType, CommonErrorType>({
      path: `/v1/setting/${variables.id}/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    })
}

