import { HttpClient, RequestParams } from '../@http-client'
import {
  CommonErrorType,
  UsebAccessTokenErrorMessageType,
  UsebAccessTokenType,
} from '../@types/data-contracts'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class UsebApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags useb
   * @name UsebAccessTokenCreate
   * @summary 유스비 액세스 토큰 발급
   * @request POST:/v1/useb/access_token/
   * @secure
   */
  usebAccessTokenCreate = (variables?: { params?: RequestParams }) =>
    this.request<
      UsebAccessTokenType,
      UsebAccessTokenErrorMessageType | CommonErrorType
    >({
      path: `/v1/useb/access_token/`,
      method: 'POST',
      secure: true,
      format: 'json',
      ...variables?.params,
    })
}

