import { useMutation, useQuery } from '@tanstack/react-query'

import { AxiosError } from 'axios'

import instance from '@/configs/axios/instance'

import {
  CommonErrorType,
  UserLoginErrorMessageType,
  UserRefreshErrorMessageType,
} from '../@types/data-contracts'
import {
  MutationHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { UserApi } from './User.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const userApi = new UserApi({ instance: instance })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_USER_API = {
  LOGIN_CREATE: () => ['USER_LOGIN_CREATE'],
  REFRESH_CREATE: () => ['USER_REFRESH_CREATE'],
  TEST_RETRIEVE: (variables?: Parameter<typeof userApi.userTestRetrieve>) =>
    ['USER_TEST_RETRIEVE', variables].filter(isDefined),
  RETRIEVE: (variables?: Parameter<typeof userApi.userRetrieve>) =>
    ['USER_RETRIEVE', variables].filter(isDefined),
}

/**
 * No description
 *
 * @tags user
 * @name UserLoginCreate
 * @summary 유저 로그인
 * @request POST:/v1/user/login/
 * @secure  */

export const useUserLoginCreateMutation = (
  params: MutationHookParams<
    typeof userApi.userLoginCreate,
    AxiosError<UserLoginErrorMessageType | CommonErrorType>
  >,
) => {
  const mutationKey = QUERY_KEY_USER_API.LOGIN_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: userApi.userLoginCreate,
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags user
 * @name UserRefreshCreate
 * @summary 유저 리프레시
 * @request POST:/v1/user/refresh/
 * @secure  */

export const useUserRefreshCreateMutation = (
  params: MutationHookParams<
    typeof userApi.userRefreshCreate,
    AxiosError<UserRefreshErrorMessageType | CommonErrorType>
  >,
) => {
  const mutationKey = QUERY_KEY_USER_API.REFRESH_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: userApi.userRefreshCreate,
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags user
 * @name UserTestRetrieve
 * @request GET:/v1/user/test/
 * @secure    */

export const useUserTestRetrieveQuery = <
  TData = RequestFnReturn<typeof userApi.userTestRetrieve>,
>(
  params?: QueryHookParams<
    typeof userApi.userTestRetrieve,
    AxiosError<CommonErrorType>,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_USER_API.TEST_RETRIEVE(params?.variables)
  return useQuery({
    queryKey,
    queryFn: () => userApi.userTestRetrieve(params?.variables),
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags user
 * @name UserRetrieve
 * @summary 유저 상세 조회
 * @request GET:/v1/user/{id}/
 * @secure    */

export const useUserRetrieveQuery = <
  TData = RequestFnReturn<typeof userApi.userRetrieve>,
>(
  params: QueryHookParams<
    typeof userApi.userRetrieve,
    AxiosError<CommonErrorType>,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_USER_API.RETRIEVE(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => userApi.userRetrieve(params.variables),
    ...params?.options,
  })
}
