import { useMutation, useQuery } from '@tanstack/react-query'

import { AxiosError } from 'axios'

import instance from '@/configs/axios/instance'

import {
  CommonErrorType,
  WetaxLoginErrorMessageType,
  WetaxOtpErrorMessageType,
} from '../@types/data-contracts'
import {
  MutationHookParams,
  Parameter,
  QueryHookParams,
  RequestFnReturn,
} from '../@types/react-query-type'
import { WetaxApi } from './Wetax.api'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

/**
 * tok-cli.config.ts 에서 설정된 instance 경로의 axios instace 가 적용된, api 의 instance 입니다.
 */
export const wetaxApi = new WetaxApi({ instance: instance })

/**
 * query key 에 undefined 를 포함시키지 않기 위한 함수입니다.
 */
const isDefined = (v: unknown) => typeof v !== 'undefined'

/**
 * query-keys
 */
export const QUERY_KEY_WETAX_API = {
  LOGIN_CREATE: () => ['WETAX_LOGIN_CREATE'],
  RETRIEVE: (variables?: Parameter<typeof wetaxApi.wetaxRetrieve>) =>
    ['WETAX_RETRIEVE', variables].filter(isDefined),
  OTP_CREATE: () => ['WETAX_OTP_CREATE'],
}

/**
 * No description
 *
 * @tags wetax
 * @name WetaxLoginCreate
 * @summary 위텍스 로그인
 * @request POST:/v1/wetax/login/
 * @secure  */

export const useWetaxLoginCreateMutation = (
  params: MutationHookParams<
    typeof wetaxApi.wetaxLoginCreate,
    AxiosError<WetaxLoginErrorMessageType | CommonErrorType>
  >,
) => {
  const mutationKey = QUERY_KEY_WETAX_API.LOGIN_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: wetaxApi.wetaxLoginCreate,
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags wetax
 * @name WetaxRetrieve
 * @request GET:/v1/wetax/{id}/
 * @secure    */

export const useWetaxRetrieveQuery = <
  TData = RequestFnReturn<typeof wetaxApi.wetaxRetrieve>,
>(
  params: QueryHookParams<
    typeof wetaxApi.wetaxRetrieve,
    AxiosError<CommonErrorType>,
    TData
  >,
) => {
  const queryKey = QUERY_KEY_WETAX_API.RETRIEVE(params.variables)
  return useQuery({
    queryKey,
    queryFn: () => wetaxApi.wetaxRetrieve(params.variables),
    ...params?.options,
  })
}

/**
 * No description
 *
 * @tags wetax
 * @name WetaxOtpCreate
 * @summary 위텍스 OTP
 * @request POST:/v1/wetax/{id}/otp/
 * @secure  */

export const useWetaxOtpCreateMutation = (
  params: MutationHookParams<
    typeof wetaxApi.wetaxOtpCreate,
    AxiosError<WetaxOtpErrorMessageType | CommonErrorType>
  >,
) => {
  const mutationKey = QUERY_KEY_WETAX_API.OTP_CREATE()
  return useMutation({
    mutationKey,
    mutationFn: wetaxApi.wetaxOtpCreate,
    ...params?.options,
  })
}
